(window.webpackJsonp=window.webpackJsonp||[]).push([[17],{348:function(v,_,p){"use strict";p.r(_);var t=p(14),e=Object(t.a)({},function(){var v=this,_=v.$createElement,p=v._self._c||_;return p("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[p("p",[v._v("隔一程山水，你是我不能回去的原乡，与我坐望于光阴的两岸。")]),v._v(" "),p("p",[v._v("彼处桃花盛开，绚烂满天凄艳的红霞，你笑得清浅从容，而我却仍在这里守望，落英如雨，印证我佛拈花一笑的了然。爱，如此繁华，如此寂寥。")]),v._v(" "),p("p",[v._v("起身，然后落座，知道，与你的缘份，也只有这一盏茶而已。结局早已先我抵达，蛰伏于五月的一场雨，十分钟，或许不够一生回忆，却足以老去所有年华。")]),v._v(" "),p("p",[v._v("五月的天空泼满青釉，你瓷青的衣襟在风里飘拂。阳光遍地，你信手拾起一枚，放进我手里，说：“我爱你！”三字成谶，我被你一语中的，从此，沉重的枷锁背负我每个梦境，明知无望，却固守着仅存的坚持，以为，终究可以将你守侯成最美的风景。")]),v._v(" "),p("p",[v._v("若青春可以作注，我已押上一切筹码，只待你开出一幅九天十地的牌久，示我以最终的输赢。谁知，你竟中途离开，衣袖随长风斜过，拂乱了赌局。无人坐庄，这一局牌宛然三月桃花，错落于五月的湖面，飘散了满湖的灰飞烟灭。")]),v._v(" "),p("p",[v._v("遂重新检视命运，看它如何写就这一段际遇。暮色四合，天边的浮云已渐暗。人走，茶亦凉，有明月，照你的背影涉水而过，十丈红尘饰你以锦绣，千朵芙蓉衣你以华裳，而你竟无半点回顾，就这样，轻易穿越我一生的沧桑。")]),v._v(" "),p("p",[v._v("摊开手掌，阳光菲薄，一如你的许诺。太爱你，所以希望你以许诺勾兑眼泪，以永恒明见柔情，却不曾料到，岁月将你的微笑做了伏笔，只待风沙四起，尘埃遍野，便折戟扬刀，杀一个回马枪，陷我于永无翻身之日的险境。")]),v._v(" "),p("p",[v._v("没有狂歌当哭的勇气，却在倒地时明心见性，瞥见万里风沙之上，有人沉腕拨镫，疾书一行字：“相忘于江湖”。朱砂如血，触目惊心。")]),v._v(" "),p("p",[v._v("忘，谈何容易？烟水亭边，你用青色丝绦挽就了我的心结，江南的水光潋滟了你的眼，你已是我一生的水源，润我干涸的视线，柔我冷硬的心痂，忘记你，不如忘记我自己。")]),v._v(" "),p("p",[v._v("而夜幕，却依旧如期降临，深冬的风替换曾经的烟花三月，举目四望，偌大的桌边只我一人，空对，一盏冰冷的茶。")]),v._v(" "),p("p",[v._v("竟是不能不忘。")]),v._v(" "),p("p",[v._v("也罢，且学你拂袖而去，菩提树下觅一方青石，静待，看沧海变桑田。")]),v._v(" "),p("p",[v._v("你已到达彼岸，水草丰美，桃花怒放，便是落雨，也有一番风细柳斜的心事。我只能做到起身离席，却仍无法与你同步。其实，又何曾与你同步过？一盏茶的爱，终我一生，也只有这一盏茶的温度，由暖而凉，片刻而已。")]),v._v(" "),p("p",[v._v("你抬手落笔，转折勾挑出青春的天书，我是你无法辩识的狂草，短短一行，被你飞快地写下，翻过。再提起，只怕也要在多年以后，由阔达圆和的魏体悄然重写，方可看清，当初的挥毫泼墨，竟是如此轻易，如此不堪。")]),v._v(" "),p("p",[v._v("回忆若能下酒，往事便可作一场宿醉，醒来时，天依旧清亮，风仍然分明，而光阴的两岸，终究无法以一苇渡杭，我知你心意。")]),v._v(" "),p("p",[v._v("无须更多言语，我必与你相忘于江湖，以沧桑为饮，年华果腹，岁月做衣锦华服，于百转千回后，悄然转身，然后，离去。")])])},[],!1,null,null,null);_.default=e.exports}}]);